import React, { useEffect, useState } from "react";
import socket from "../socket";
import axios from "axios";

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fonction pour r√©cup√©rer les t√¢ches
  const fetchTasks = async () => {
    setLoading(true);
    setError(null);

    try {
      const res = await axios.get("http://localhost:5000/tasks");
      console.log("‚úÖ T√¢ches r√©cup√©r√©es :", res.data); // Debug
      setTasks(res.data);
    } catch (err) {
      console.error("‚ùå Erreur de r√©cup√©ration :", err);
      setError("Impossible de r√©cup√©rer les t√¢ches.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTasks(); // Charger les t√¢ches au d√©marrage

    // √âcouter les √©v√©nements Socket.io
    const handleSocketNotification = (data) => {
      console.log("üîî Notification re√ßue :", data);
      setTasks((prev) => {
        if (data.type === "new") return [...prev, data.task];
        if (data.type === "update") return prev.map(task => task._id === data.task._id ? data.task : task);
        if (data.type === "delete") return prev.filter(task => task._id !== data.task._id);
        return prev;
      });
    };

    if (socket) {
      socket.on("taskNotification", handleSocketNotification);
    }

    return () => {
      if (socket) {
        socket.off("taskNotification", handleSocketNotification);
      }
    };
  }, []);

  return (
    <div>
      <h2>Liste des t√¢ches</h2>

      {loading && <p>Chargement des t√¢ches...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      {tasks.length === 0 && !loading && !error && <p>Aucune t√¢che disponible.</p>}

      <ul>
        {tasks.map((task) => (
          <li key={task._id}>
            <strong>{task.title}</strong> - {task.status}
          </li>
        ))}
      </ul>

      <button onClick={fetchTasks} style={{ marginTop: "10px" }}>
        üîÑ Recharger les t√¢ches
      </button>
    </div>
  );
};

export default TaskList;
