import React, { useState } from "react";
import axios from "axios";

const TaskForm = () => {
  const [title, setTitle] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!title) return;

    try {
      const res = await axios.post("http://localhost:5000/tasks", { title }, { withCredentials: true });
      setTitle("");
    } catch (err) {
      console.error("Erreur d'ajout de tâche", err);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} placeholder="Nouvelle tâche" />
      <button type="submit">Ajouter</button>
    </form>
  );
};

export default TaskForm;
