import React, { useEffect, useState } from "react";
import socket from "../socket";
import axios from "axios";

const TaskList = () => {
  const [tasks, setTasks] = useState([]);

  // Fonction pour récupérer les tâches
  const fetchTasks = async () => {
    try {
      const res = await axios.get("http://localhost:5000/tasks", { withCredentials: true });
      console.log("Tâches récupérées :", res.data);
      setTasks(res.data);
    } catch (err) {
      console.error("Erreur lors de la récupération des tâches :", err);
    }
  };

  useEffect(() => {
    fetchTasks(); // Charger les tâches au démarrage

    // Vérifier que le socket est bien défini avant d'ajouter des écouteurs
    if (socket) {
      socket.on("taskNotification", (data) => {
        console.log("Nouvelle notification :", data);
        setTasks((prev) => {
          if (data.type === "new") return [...prev, data.task];
          if (data.type === "update") return prev.map(task => task._id === data.task._id ? data.task : task);
          if (data.type === "delete") return prev.filter(task => task._id !== data.task._id);
          return prev;
        });
      });
    }

    return () => {
      if (socket) {
        socket.off("taskNotification");
      }
    };
  }, []); // Exécuter `useEffect` une seule fois au montage

  return (
    <div>
      <h2>Liste des tâches</h2>
      {tasks.length === 0 ? <p>Aucune tâche disponible.</p> : (
        <ul>
          {tasks.map((task) => (
            <li key={task._id}>
              {task.title} - {task.status}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default TaskList;
